import React, { useEffect, useState } from "react";
import "./StoreList.css"; // ðŸ‘ˆ import CSS file

export default function StoreList() {
  const [stores, setStores] = useState([]);
  const [search, setSearch] = useState("");

  // Fake store data
  const allStores = [
    { id: 1, name: "SuperMart", address: "Main Road", avgRating: 4 },
    { id: 2, name: "FreshFoods", address: "High Street", avgRating: 3 },
    { id: 3, name: "Daily Needs", address: "Market Area", avgRating: 5 },
  ];

  // Just filters local data instead of API call
  const loadStores = () => {
    const filtered = allStores.filter(
      (s) =>
        s.name.toLowerCase().includes(search.toLowerCase()) ||
        s.address.toLowerCase().includes(search.toLowerCase())
    );
    setStores(filtered);
  };

  // Load initial stores
  useEffect(() => {
    setStores(allStores);
  }, []);

  return (
    <div style={{ padding: 20 }}>
      <h2>Stores</h2>
      <input
        placeholder="Search stores"
        value={search}
        onChange={(e) => setSearch(e.target.value)}
        style={{ marginRight: "10px" }}
      />
      <button onClick={loadStores}>Search</button>

      <ul style={{ marginTop: "20px" }}>
        {stores.map((s) => (
          <li key={s.id}>
            <b>{s.name}</b> â€” {s.address} â€” Avg Rating: {s.avgRating}
          </li>
        ))}
      </ul>
    </div>
  );
}
